DROP TABLE employee;
CREATE TABLE employee(
   id             VARCHAR(3) PRIMARY KEY
  ,name           VARCHAR(13) 
  ,first_surname  VARCHAR(12) 
  ,second_surname VARCHAR(12) 
  ,phone          INTEGER  
  ,email          VARCHAR(21) 
  ,job            VARCHAR(26) 
  ,hiring_date    TIMESTAMP 
  ,year_salary    INTEGER  
  ,sick_leave     BOOLEAN
);
